import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Auth.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("learner");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Mock user database (replace with real API calls later)
  const mockUsers = [
    {
      email: "student@example.com",
      password: "password123",
      role: "learner",
      fullName: "John Doe",
      age: "21"
    },
    {
      email: "instructor@example.com",
      password: "password123",
      role: "instructor",
      fullName: "Jane Smith",
      age: "35"
    }
  ];

  const handleLogin = (e) => {
    e.preventDefault();
    setError("");

    // Find user in mock data (replace with API call)
    const user = mockUsers.find(
      (u) => u.email === email && u.password === password && u.role === role
    );

    if (user) {
      // Store user data (without password)
      const { password: _, ...userData } = user;
      localStorage.setItem("userData", JSON.stringify(userData));
      localStorage.setItem("authToken", "mock-token");

      // Redirect based on role
      navigate(role === "learner" ? "/profile" : "/instructor-dashboard");
    } else {
      setError("Invalid credentials. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1 className="login-title">DriveMaster</h1>
        <h2 className="login-subtitle">Login to Your Account</h2>
        
        {error && <div className="error-message">{error}</div>}

        <form className="login-form" onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              placeholder="Your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              placeholder="Your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label>Role</label>
            <select value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="learner">Learner</option>
              <option value="instructor">Instructor</option>
            </select>
          </div>

          <button className="login-button" type="submit">
            Login
          </button>
          
          <p className="register-text">
            Don't have an account?{" "}
            <span className="register-link" onClick={() => navigate("/register")}>
              Register now
            </span>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Login;
